sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var Supervisor = current.supervisor;
	var Budget = current.budget_approver;
	var erAgent = current.agent_er.user;
	var lrAgent = current.agent_lr.user;

	var watch_list = current.watch_list.toString().split(",");

	if (watch_list.toString().indexOf(Supervisor) == -1) {
		   watch_list.push(Supervisor);
		   current.watch_list = watch_list.join(",").toString();
	}

	watch_list = current.watch_list.toString().split(",");
	if (watch_list.toString().indexOf(Budget) == -1) {
		   watch_list.push(Budget);
		   current.watch_list = watch_list.join(",").toString();
	}

	watch_list = current.watch_list.toString().split(",");
	if (watch_list.toString().indexOf(erAgent) == -1) {
		   watch_list.push(erAgent);
		   current.watch_list = watch_list.join(",").toString();
	}

	watch_list = current.watch_list.toString().split(",");
	if (watch_list.toString().indexOf(lrAgent) == -1) {
		   watch_list.push(lrAgent);
		   current.watch_list = watch_list.join(",").toString();
	}


})(current, previous);
