sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue === '') {
	return;
	}
var ga = new GlideAjax('employeeEIDScript');
      ga.addParam('sysparm_name', 'getEID');
      ga.addParam('sysparm_employeeName', g_form.getValue("employee"));
      ga.getXML(updateCampus);
}

function updateCampus(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var clearvalue; // Stays Undefined

    if (answer) {
        var returneddata = answer.evalJSON(true);
        g_form.setValue("employee_eid", returneddata.eid);
    } 
    else {
        g_form.setValue("employee_eid", clearvalue);
    }
}
